    static class BlogService
    {
        static List<Blog> blogs = new List<Blog>();
        static BlogService()
        {
            blogs.Add(new Blog
            {
                Author = "admin",
                Content = "<h2 style='box-sizing: inherit; margin: 25px 0px 15px; font-weight: 600; font-size: 1.7em; line-height: 1.3; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>1. Phở</h2>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;'><img alt='' height='538' src='https://topbienhoa.com/public/userfiles/images/an-uong/pho.webp' width='1024' class='img-fluid' loading='lazy' style='box-sizing: inherit; vertical-align: middle; border-style: none; max-width: 100%; height: auto; border-radius: 5px;'></p>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>Phở về cơ bản là món ăn đặc trưng của Việt Nam, bao gồm mì gạo trong nước súp đậm đà với thịt và các loại rau xanh, cùng với nước chấm (nước mắm) hoặc tương ớt.&nbsp;Một bát cơ bản gồm có tai (thịt bò thái mỏng), boa-rô (thịt bò viên) hoặc nạm (sườn bò), bên trên là giá đỗ, chanh và các loại rau thơm tươi như húng quế, bạc hà, ngò và hành.&nbsp;Tùy thuộc vào nhà hàng hoặc quán ven đường, bạn cũng có thể lựa chọn các nguyên liệu lạ miệng hơn như gân (gân bò), sách (bao tử heo thái mỏng) và&nbsp;xương.&nbsp;Thường được ăn vào bữa sáng, phở có giá từ 30.000 đến 50.000 đồng tại một nhà hàng địa phương hoặc chợ đường phố ở Việt Nam.</p>\n<h2 style='box-sizing: inherit; margin: 25px 0px 15px; font-weight: 600; font-size: 1.7em; line-height: 1.3; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>2. Bánh Mì</h2>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'><img alt='' height='575' src='https://topbienhoa.com/public/userfiles/images/an-uong/banh-mi.webp' width='1024' class='img-fluid' loading='lazy' style='box-sizing: inherit; vertical-align: middle; border-style: none; max-width: 100%; height: auto; border-radius: 5px;'></p>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>Bánh mì là một loại bánh mì Pháp - Việt độc đáo, rất phù hợp khi bạn cần một bữa ăn nhanh. Có giá từ 10.000 đến 15.000 đồng, một suất bánh mì xíu mại nướng, rau củ ngâm, pate, bơ, nước tương, ngò, tương ớt và ớt xay. Hầu hết những người bán bánh mì cũng cung cấp nhiều loại nhân thịt, bao gồm heo quay,&nbsp;trứng ốp la (trứng chiên), thịt nướng (thăn heo nướng), chả cá (cá chiên với nghệ và thì là), chả lụa, xá&nbsp;xíu&nbsp;và thịt gà.</p>\n<h2 style='box-sizing: inherit; margin: 25px 0px 15px; font-weight: 600; font-size: 1.7em; line-height: 1.3; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>3.&nbsp;Bánh Xèo</h2>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'><img alt='' height='577' src='https://topbienhoa.com/public/userfiles/images/an-uong/banh-xeo.webp' width='1024' class='img-fluid' loading='lazy' style='box-sizing: inherit; vertical-align: middle; border-style: none; max-width: 100%; height: auto; border-radius: 5px;'></p>\n<p style='box-sizing: inherit; font-size: 17px; line-height: 1.6; color: rgb(0, 0, 0); font-family: Quicksand; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;'>Bánh xèo được làm từ bột gạo, nước cốt dừa và nghệ, bạn có thể ăn kèm với bún, thịt gà, thịt lợn hoặc thịt bò thái mỏng, tôm, hành tây thái mỏng, giá đỗ và nấm. Hầu hết các quán ven đường, chợ dân sinh và nhà hàng đều có bán một đĩa bánh xèo với giá khoảng 5.000 đến 25.000 đồng, thường đi kèm với rau xà lách hoặc bánh tráng tươi. Ăn như người bản xứ bằng cách gói bánh xèo trong lá cải xanh, lá xà lách hoặc bánh tráng cùng với nem lụi, lá bạc hà, húng quế, trước khi chấm với nước sốt đậu phộng (Mắm nêm)</p>",
                Date = DateTime.Now.ToString(),
                Description = "Những món ăn được người dùng đánh giá cao",
                Img = "Profile.png",
                Title = "Top 3 Món ăn ngon nhất Việt Nam"
            });
